    public static ArrayList<Basah> getListDAta() {
        Basah basah = null;
        ArrayList<Basah> list = new ArrayList<>();
        for (int i = 0; i < data.length; i++) {
            basah = new Basah();
            basah.setName(data[i][0]);
            basah.setBahan(data[i][1]);
            basah.setDate(data[i][2]);
            basah.setFoto(data[i][3]);
            basah.setCara(data[i][4]);

            list.add(basah);
        }
        return list;

    }
